#let discrete-plot(
  ys: (),
  x-label: $x$,
  y-label: $y$,
  width: 2cm,
  x-ticks: (),
  y-ticks: (),
) = {
  let n = ys.len()

  cetz.canvas(
    length: width,
    {
      import cetz.draw: *
      import cetz-plot: *

      let x = 0
      plot.plot(
        size: (1, 1),
        y-max: 1,
        x-max: 1 + 1 / n,
        x-tick-step: none,
        y-tick-step: 1,
        x-label: x-label,
        y-label: y-label,
        {
          for i in range(n) {
            x += 1 / n
            plot.annotate(line((x, 0), (x, ys.at(i)), stroke: accent-color))
            plot.annotate(
              content(
                (x + 0.2, ys.at(i) + 0.1),
                angle: 60deg,
                anchor: "mid-east",
                x-ticks.at(i, default: []),
              ),
            )
            plot.add(
              ((x, ys.at(i)),),
              mark: "o",
              mark-size: 0.1,
              hypograph: true,
            )
          }
        },
      )
    },
  )
}

