          }
          for i in range(first-iteration-to-label, x.len()) {
            add-anchor("x" + str(i), (x.at(i), 0))
            add-anchor("fx" + str(i), (x.at(i), f(x.at(i))))
          }
        },
      )
      for i in range(first-iteration-to-label, x.len()) {
        cetz.draw.line(
          plot-name + ".x" + str(i),
          plot-name + ".fx" + str(i),
          stroke: (paint: gray, dash: "dashed"),
        )
        cetz.draw.content(
          plot-name + ".fx" + str(i),
          eval(mode: "math", "f(x^\"(" + str(i) + ")\")"),
          anchor: approx-label-anchor,
        )
      }
    })

