#set text(size: 16pt)

// Can be specified via CLI.
#let sac_end_x = float(sys.inputs.at("saccade_amplitude", default: 8))

// For text annotations next to a visual element.
#let label_offset_xy = (x: 1.5, y: 1.25);
#let annotation_size = 14pt

#let annotate_location(loc) = {
  content(
    (loc.x + label_offset_xy.x, loc.y + label_offset_xy.y),
    text(size: annotation_size, [(#loc.x, #loc.y)])
  );
}


#let draw-flash-marker(pos, alpha: 100%) = {
  let circle-radius = 0.35;
  let ray-length = 0.5;
  let num-rays = 10;

  // Orange with some transparency as needed
  let color = oklab(79.08%, 0.1567, 64.63%, alpha)
  circle(pos, radius: circle-radius, stroke: 0.4pt + color, fill: color);
  for i in range(0, num-rays) {
    let angle = i * 360deg / num-rays;
    let start = (
      pos.x + circle-radius * calc.cos(angle),
      pos.y + circle-radius * calc.sin(angle)
    );
    let end = (
      pos.x + (circle-radius + ray-length) * calc.cos(angle),
      pos.y + (circle-radius + ray-length) * calc.sin(angle)
    );
    line(start, end, stroke: 1pt + color);
  }
};



#let draw_real_flash(pos) = draw-flash-marker(pos, alpha: 100%)
#let draw_ghost_flash(pos) = draw-flash-marker(pos, alpha: 50%)

#let fixation(content, ..extra) = text(
  size: 20pt,
  weight: "bold",
  font: "DejaVu Sans",
  ..extra,
  content
)


// Autoscale output, without margins.
#set page(width: auto, height: auto, margin: 0%)

#cetz.canvas(
length: 0.75cm,
  {
  import cetz.draw: *

  let start_content = fixation("S")
  let end_content = fixation("E", fill: rgb("#008000"))

  let main_arrow_thickness = 4pt;
  let sac_start = (x: 0, y: 0);
  let sac_end   = (x: sac_end_x, y: 0);

  let max_x = sac_end_x + 3
  let max_y = 13.5

  set-style(
    mark: (fill: black, scale: 2),
    stroke: (thickness: 0.4pt, cap: "round")
  );

  // Draw x, y axes, with arrows at the end, and x/y labels
  let axis_overflow = 2
  line((-axis_overflow, 0), (max_x, 0), mark: (end: "stealth"), stroke: 1.5pt);
  content((max_x - 0.5, -0.8), $x$);
  line((0, -axis_overflow), (0, max_y), mark: (end: "stealth"), stroke: 1.5pt);
  content((0.8, max_y - 0.5), $y$);

  // Draw start and end fixations.
  // Both are a single big letter within a circle, with coordinate annotations
  let draw_fixation(loc, inner) = {
      circle(loc, fill: white, radius: 1);
      content(loc, inner);
      annotate_location(loc);

  }
  draw_fixation(sac_start, start_content)
  draw_fixation(sac_end, end_content)


  // Draw the fat saccade arrow.
  let draw_fat_arrow(start, end) = {
    line(
      start,
      end,
      mark: (end: "stealth"),
      stroke: (thickness: main_arrow_thickness)
    );
  }
  draw_fat_arrow(
    (x: 1.1, y: - 1),
    (x: sac_end_x - 1.1, y: - 1)
  )

  // Annotate that arrow
  content(
    (
      // Centered
      sac_end_x / 2,
      //  under the arrow
      -3
    ),
    align(center)[Rightward saccade \ at $t = 0$ ms]
  )


  // Draw the flashes
  let real_flash_pos = (x: 0, y: 10);
  let max_misloc = 3.5
  let forward_flash_pos = (x: max_misloc, y: real_flash_pos.y)
  let backward_flash_pos = (x: -max_misloc, y: real_flash_pos.y)

  let box_annot(body, loc) = {
    content(
      (loc.x, loc.y - 2),
      box(
        align(
          center + horizon,
          text(
            body,
          )
        ),
        fill: white,
        //stroke: black,
        radius: 2pt,
        inset: 3pt
      )
    )
  }

  // Real flash
  draw_real_flash(real_flash_pos)
  annotate_location(real_flash_pos)
  box_annot([True \ position], real_flash_pos)


  // Forward ghost
  draw_ghost_flash(forward_flash_pos)
  annotate_location(forward_flash_pos)
  box_annot([Perceived \ forward], forward_flash_pos)

  // Backward ghost
  draw_ghost_flash(backward_flash_pos)
  annotate_location(backward_flash_pos)
  box_annot([Perceived \ backward], backward_flash_pos)
})

#import "@preview/fletcher:0.5.7" as fletcher

// --- Configuration ---
#let timeline-abs-width = 25cm
#let start-year = 1940
#let end-year = 2030
#let y-offset = 1.5cm // Base vertical offset distance
#let marker-radius = 6pt
#let timeline_width = 3pt
#let conn_width = 1.5pt

// Colors
#let main-color = blue.darken(20%)
#let marker-color = orange.darken(10%)
#let anno-color-1 = teal.darken(30%)
#let anno-color-2 = purple.darken(20%)



// --- Helper function to calculate X position ---
#let get-x-pos(year, width) = {
  let factor = (year - start-year) / (end-year - start-year)
  factor * width
}

// --- Create the timeline ---
#let make_timeline(events) = align(center + horizon)[
  #set text(size: 14pt)

  // Use fletcher.diagram()
  #fletcher.diagram(
    {
    // --- Define invisible nodes for timeline ends ---
    fletcher.node(
      (0cm, 0cm),
      name: <tl-start>,
      fill: none, stroke: none
    )
    fletcher.node(
      (timeline-abs-width, 0cm),
      name: <tl-end>,
      fill: none, stroke: none
    )

    // --- Draw the main timeline axis ---
    fletcher.edge(
      <tl-start>, <tl-end>,
      "->",
      stroke: main-color + timeline_width,
    )

    // --- Add event markers and annotations ---
    for (i, event) in events.enumerate() {
      // Calculate positions and setup using the y_mult factor
      let x-pos = get-x-pos(event.year, timeline-abs-width)
      let marker-coord = (x-pos, 0cm)
      // Calculate offset based on multiplier
      let current-y-offset = event.y_mult * y-offset
      let anno-coord = (x-pos, current-y-offset)
      // Determine anchor based on positive/negative offset
      let anno-anchor = if event.y_mult > 0 { "south" } else { "north" }
      // Determine color based on positive/negative offset
      // let anno-color = if event.y_mult > 0 { anno-color-1 } else { anno-color-2 }
      let anno-color = anno-color-2
      let marker-name = label("marker-" + str(i))
      let anno-name = label("anno-" + str(i))

      // Define marker node
      fletcher.node(
        marker-coord,
        name: marker-name,
        fill: marker-color,
        shape: "circle",
        radius: marker-radius,
        stroke: none
      )

      // Define annotation node
      fletcher.node(
        anno-coord,
        name: anno-name,
        align(center)[
          #text(weight: "bold", anno-color)[#event.year] \
          #text(weight: "bold", event.title) \
          #emph(event.citation) // Keeping citation emphasized
        ]
      )

      // Connect marker to annotation
      fletcher.edge(
        marker-name,
        (name: anno-name, anchor: anno-anchor),
        stroke: (paint: anno-color, thickness: conn_width, dash: "dashed"),
      )
    }
  }) // End of diagram content
]


